mapper.insert:
INSERT INTO notices (notice_no, admin_id, notice_title, notice_content, 
                     notice_writing_time, notice_modifying_time, notice_view_cnt) 
      VALUES (notice_no_SEQ.NEXTVAL
            , #{adminId}
            , #{noticeTitle}
            , #{noticeContent}
            , SYSDATE
            , null
            , 0)

mapper.update:
UPDATE notices

   notice_title = #{noticeTitle}
 , notice_content = #{noticeContent}
 , notice_modifying_time = SYSDATE  

WHERE notice_no = #{noticeNo}

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY 1 ORDER BY 1) AS r, a.* FROM (SELECT notice_no, admin_id, notice_title, notice_writing_time, notice_modifying_time, notice_view_cnt FROM notices
notice_title LIKE '%${keyword}%' OR notice_content LIKE '%${keyword}%'
ORDER BY notice_no DESC ) a ) WHERE r BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE notices

   notice_view_cnt = notice_view_cnt + 1  

WHERE notice_no = #{noticeNo}
